import React, { useEffect } from 'react';

const ScrollDrawing = () => {
  useEffect(() => {
    const triangle = document.getElementById("triangle");
    const length = triangle.getTotalLength();
    triangle.style.strokeDasharray = length;
    triangle.style.strokeDashoffset = length;

    const myFunction = () => {
      const scrollpercent = (window.scrollY) / (document.documentElement.scrollHeight - window.innerHeight);
      const draw = length * scrollpercent;
      triangle.style.strokeDashoffset = length - draw;
    };

    window.addEventListener("scroll", myFunction);
    return () => {
      window.removeEventListener("scroll", myFunction);
    };
  }, []);

  return (
    <div className="bg-gray-100 min-h-screen p-8 overflow-y-scroll">
      <h2 className="text-2xl font-bold">Scroll down this window to draw a triangle.</h2>
      <p className="text-2xl font-bold mt-8">Scroll back up to reverse the drawing.</p>
      <div className="h-[600px]">
      </div>

        <svg id="mySVG">
          <path fill="none" stroke="red" strokeWidth="3" id="triangle" d="M150 0 L75 200 L225 200 Z" />
          Sorry, your browser does not support inline SVG.
        </svg>
    </div>
  );
};

export default ScrollDrawing;
